

update DEPARTMENT set MANAGER_ID = (select EMPLOYEE_ID from EMPLOYEE where FIRST_NAME = 'John') where DEPARTMENT_ID =(select DEPARTMENT_ID from DEPARTMENT where DEPARTMENT_NAME = 'ADMIN');
update DEPARTMENT set MANAGER_ID = (select EMPLOYEE_ID from EMPLOYEE where FIRST_NAME = 'Sam') where DEPARTMENT_ID =(select DEPARTMENT_ID from DEPARTMENT where DEPARTMENT_NAME = 'IT');
update DEPARTMENT set MANAGER_ID = (select EMPLOYEE_ID from EMPLOYEE where FIRST_NAME = 'August') where DEPARTMENT_ID =(select DEPARTMENT_ID from DEPARTMENT where DEPARTMENT_NAME = 'HR');

